package cabra.dinero;

import cabra.*;
import javax.swing.*;
import java.awt.*;
import java.awt.event.*;
import java.util.ArrayList;
import java.util.Arrays;
import sun.awt.WindowClosingListener;

/**
 * Controller class for the Vault.
 * It receives input from the view (purchasePrize()), changes the model, and updates the view.
 * 
 * Most-commonly-used public methods:
 * int getPoints()
 * boolean purchasePrize()
 */
public class VaultManager {

    private PointManager pointManager;
    private ArrayList<Prize> purchasedPrizes = new ArrayList<Prize>();

    private GUI gui;
    private VaultTabPane tabPane;
    private JLabel pointLabel;

    private final static int MAX_POINTS_DIGITS = 5; //points label will guaranteed have this many digits (00055 for example)

    /**
     * Usage:
     * Create a VaultManager when you want to start up the vault.
     */
    public VaultManager(PointManager pointManager, final GUI gui, JLabel pointLabel) {
        this.gui = gui;
        this.pointManager = pointManager;
        pointManager.giveVaultManager(this);
        this.pointLabel = pointLabel;

        //stock points/prizes with the data stored
        String[] prizeArray = Utils.arrayFromString(UserData.getString("PrizesBought"));
        for (String prizeString : prizeArray) {
            Prize prize = Prize.getPrizeByID(prizeString); //prizeString is unique ID of prize
            if (prize != null) {
                purchasedPrizes.add(prize);
            }
        }

        //init tabPane
        tabPane = new VaultTabPane(this);

        //init view
        /*frame = new JFrame("Cabra Vault");
        frame.setIconImages(ImageManager.createImages("safe.png", "safe-16.png"));
        frame.add(tabPane);
        frame.setSize(new Dimension(WIDTH, HEIGHT));
        frame.setLocationRelativeTo(null); //center on screen
        frame.addWindowListener(new WindowAdapter(){
            @Override
            public void windowClosing(WindowEvent e){
                //update the main gui
                //gui.refresh(); //buggy
            }
        });*/


        //<TODO>: PUT IN MAIN PANEL
    }

    /**
     * Returns a panel containing stuff related to THIS vault manager (the tab pane.)
     * @return 
     */
    public JPanel getPanel(){
        JPanel holder = new JPanel();
        holder.add(tabPane);
        return holder;
    }    

    /**
     * Returns the frame this is contained in.
     * @return the frame that the vault stuff is in.
     */
    public JFrame getFrame(){ return gui.getFrame(); }

    public int getPoints() {
        return pointManager.getPoints();
    }



    /**
     * Decreases the number of points the user has by the given amount.
     * This just redirects to 
     */
    private void decreasePoints(int pointsToLose) {
        pointManager.decreasePoints(pointsToLose);
        update();
    }

    /**
     * Updates based on how many points the user has.
     */
    public void update(){
        updatePointLabel(pointManager.getPoints());
        tabPane.getVaultPanel().prizePurchased();
        tabPane.getStorePanel().pointsChanged(pointManager.getPoints());
    }    

    /***
     * Refreshes the points label so it reflects the number of points the user has.
     * @param points how many points the user has.
     */
    public void updatePointLabel(int points){
        //put zeroes in front so it looks like an old-style arcade display; can be disabled
        //String pointText = Utils.padWithLeadingZeroes(points, MAX_POINTS_DIGITS);
        String pointText = points + "";
        //string pointText = points + ""; //for the usual representation
        String text = "<html><center><b>" + pointText + "</b>";
        pointLabel.setText(text);
        pointLabel.setToolTipText("You've earned " + points + " coins while studying using Cabra!");   
    }

    /**
     * Purchases the prize pack. The user's points are decreased by the pack's cost,
     * and the prizes contained in the pack is added to the list of purchased prizes.
     * 
     * @param prize the prize pack to purchase
     * @return true if the user can afford the prize pack (and bought it), false if ther user can't afford it
     */
    public boolean purchasePrizePack(PrizePack pack) {
        if (canUserAfford(pack) == false) {
            return false; //user can't afford
        }
        //buy it
        decreasePoints(pack.getCost());
        for (Prize prize : pack.retrievePrizes()) {
            this.purchasedPrizes.add(prize);
            //tabPane.prizePurchased(prize);

            System.out.println(prize.getName() + " - " + prize.getRarity());
        }

        /*
        //remove duplicates; duplicates will be adjacent to each other
        for(int i=purchasedPrizes.size()-1; i>0; i--){
        if(purchasedPrizes.get(i).equals(purchasedPrizes.get(i-1)))
        purchasedPrizes.remove(i);
        }
         */

        //Save the list of prizes; it's overwritten so write ALL that we have
        //convert each to string first; use its ID
        int numPrizes = purchasedPrizes.size();
        String[] ids = new String[numPrizes];
        for (int i = 0; i < numPrizes; i++) {
            ids[i] = purchasedPrizes.get(i).getID();
        }
        String stringifiedIDs = Utils.stringFromArray(ids);
        UserData.setString("PrizesBought", stringifiedIDs);
        System.out.println("Saved prize data: " + stringifiedIDs);

        update();

        return true;
    }

    /**
     * Convenience method. Returns true if the user can afford the given prize pack (has enough points), false otherwise.
     */
    public boolean canUserAfford(PrizePack pack) {
        return pack.canUserAfford(getPoints());
    }

    /**
     * Returns a list of all prizes that have NOT been purchased.
     * They may or may not be affordable.
     */
    public ArrayList<Prize> getPurchaseablePrizes() {
        ArrayList<Prize> purchaseable = new ArrayList<Prize>();
        //for each of the prizes, check if it's in the purchased prizes list
        for (Prize prize : Prize.getAllPrizes()) {
            if (purchasedPrizes.contains(prize) == false) {
                //not purchased
                purchaseable.add(prize);
            }
        }

        return purchaseable;
    }

    /**
     * Returns a list of all prizes that the user has bought. These are, therefore, not available for purchase.
     */
    public ArrayList<Prize> getPurchasedPrizes() {
        return purchasedPrizes;
    }
}

package cabra.dinero;

import cabra.*;
import javax.swing.*;
import java.awt.*;
import java.awt.event.*;
import java.util.ArrayList;

/**
 * Displays a list of all prizes available for purchase.
 */
public class StorePanel extends JPanel {

    private VaultManager vaultManager; //the controller
    private ArrayList<PrizePackPanel> prizePackPanels = new ArrayList<PrizePackPanel>();

    public StorePanel(VaultManager vaultManager) {
        this.vaultManager = vaultManager;

        //init view
        setLayout(new BoxLayout(this, BoxLayout.PAGE_AXIS));

        JPanel prizePackPanel = createPrizePackPanel();
        //wrap in scroll pane
        JScrollPane scrollPane = new JScrollPane(prizePackPanel);
        scrollPane.setPreferredSize(new Dimension(VaultTabPane.MY_WIDTH, VaultTabPane.MY_HEIGHT));
        scrollPane.setVerticalScrollBarPolicy(ScrollPaneConstants.VERTICAL_SCROLLBAR_ALWAYS);
        scrollPane.setHorizontalScrollBarPolicy(ScrollPaneConstants.HORIZONTAL_SCROLLBAR_AS_NEEDED);
        add(scrollPane);
    }

    /**
     * Called when a prize(s) is purchased.
     * This updates the points label and the point pack grid based on what the user can afford.
     * @param newPoints the new number of points the user has.
     */
    public void pointsChanged(int newPoints) {
        //tell vault manager
        vaultManager.updatePointLabel(newPoints);

        //tell each prize pack panel to update - it may have become unaffordable
        for (PrizePackPanel panel : prizePackPanels) {
            panel.updateBuyButton();
        }
    }

    /**
     * Creates a panel containing stack of JPanels, each containing one prize pack available for purchase.
     */
    private JPanel createPrizePackPanel() {
        final int PADDING = 0;
        final int NUM_PACKS_ROW = 2; //also num cols

        JPanel panel = new JPanel(new GridLayout(0, NUM_PACKS_ROW, PADDING, PADDING)); //as many rows as necessary
        //make a panel for each prize pack you can buy
        for (PrizePack pack : PrizePack.getAllPrizePacks()) {
            PrizePackPanel packPanel = new PrizePackPanel(pack);
            prizePackPanels.add(packPanel); //for future reference
            panel.add(packPanel);
        }

        return panel;
    }

    /***
     * A panel used to display one prize pack in the store. It shows its rarity and cost, and has a button to buy it.
     */
    private class PrizePackPanel extends JPanel {

        private PrizePack prizePack;
        private JButton buyButton;
        private static final int PADDING = 5; //between elements
        private static final int MAX_STARS = 5; //rarest pack can have this many stars

        public PrizePackPanel(PrizePack prizePack) {
            this.prizePack = prizePack;

            //init
            setLayout(new GridBagLayout());
            GridBagConstraints c = new GridBagConstraints();
            c.anchor = GridBagConstraints.CENTER;
            c.insets = new Insets(PADDING, PADDING, PADDING, PADDING);
            c.gridx = 0;
            c.gridy = 0;
            c.gridwidth = 1;
            c.gridheight = 1;

            //image of pack
            //c.weighty = 1;
            //put in label
            JLabel packLabel = new JLabel(ImageManager.createImageIcon("box-close.png"));
            //packLabel.setAlignmentX(JLabel.CENTER_ALIGNMENT);
            add(packLabel, c);

            //stars representing the rarity
            //c.weighty = 0;
            c.gridx++;
            JPanel starPanel = new JPanel(new GridLayout(1, 0)); //as many cols as necessary, no padding
            int numStars = prizePack.getStars(MAX_STARS);
            starPanel.setToolTipText("This pack has a rarity of " + numStars + " star(s).");
            //put labels in here, one per star
            for (int i = 0; i < numStars; i++) {
                //put in a label
                JLabel label = new JLabel(ImageManager.createImageIcon("star.png"));
                starPanel.add(label);
            }
            add(starPanel, c);

            //label representing the rarity
            c.gridx = 0;
            c.gridy++;
            JLabel pointLabel = new JLabel(ImageManager.createImageIcon("coins.png"));
            pointLabel.setText(prizePack.getCost() + "");
            pointLabel.setToolTipText("This pack costs " + prizePack.getCost() + " points.");
            //pointLabel.setAlignment
            add(pointLabel, c);

            //button to buy it
            c.gridx++;
            buyButton = new JButton();
            buyButton.addActionListener(new BuyListener(prizePack));
            updateBuyButton();
            add(buyButton, c);
        }

        /**
         * Updates the buy button (which lets the user buy the pack) based on whether or not the user can afford it.
         */
        public void updateBuyButton() {
            if (vaultManager.canUserAfford(prizePack)) {
                //user can afford, make it clickable and say buy
                buyButton.setText("Buy pack");
                buyButton.setIcon(ImageManager.createImageIcon("coin-gold.png"));
                buyButton.setToolTipText("Contains 3 prizes");
            } else {
                //use can't afford, disable it
                buyButton.setText("Can't afford");
                //buyButton.setIcon(ImageManager.createImageIcon("x.png"));
                buyButton.setIcon(null);
                buyButton.setToolTipText(null);
                buyButton.setEnabled(false);
            }
        }

        private class BuyListener implements ActionListener {

            private PrizePack prizePack;

            public BuyListener(PrizePack prizePack) {
                this.prizePack = prizePack;
            }

            public void actionPerformed(ActionEvent e) {
                //are they sure?
                String confirmText = "Are you sure you want to spend " + prizePack.getCost() + " points buying this prize pack?";
                if (InputManager.confirm(confirmText, null)) {
                    //show dialog containing the pack opening and the prizes they won coming out
                    JPanel panel = new PackOpenPanel(prizePack);
                    //wrap in dialog
                    JDialog dialog = Utils.putPanelInDialog(panel, null, 
                            "Opening the prize pack... here's what you found!",
                            "box-open.png", PackOpenPanel.MY_WIDTH, PackOpenPanel.MY_HEIGHT);
                    dialog.setVisible(true);

                    //tell vault manager to buy this
                    vaultManager.purchasePrizePack(prizePack);
                }
            }
        }

        /**
         * A panel to show a prize pack being opened - showing the prizes that came out of it
         */
        private class PackOpenPanel extends JPanel {

            private PrizePack prizePack;
            public static final int MY_WIDTH = 400;
            public static final int MY_HEIGHT = 150;
            private static final int PADDING = 25; //between edges and panels
            private static final int IPADDING = 40; //internal padding; between panels

            public PackOpenPanel(PrizePack prizePack) {
                this.prizePack = prizePack;

                //setInsets(new Insets(PADDING, PADDING, PADDING, PADDING));
                setLayout(new GridBagLayout());
                GridBagConstraints c = new GridBagConstraints();
                c.insets = new Insets(PADDING, PADDING, PADDING, PADDING);
                c.ipadx = IPADDING;
                c.ipady = IPADDING;

                /*
                //add opening box icon
                JPanel imagePanel = new JPanel(new GridLayout(1,1));
                imagePanel.add(new JLabel(ImageManager.createImageIcon("box-open.png")));
                add(imagePanel, c);
                 */

                //add prize panels for each of the prizes contained in the pack
                for (Prize prize : prizePack.retrievePrizes()) {
                    PrizePanel panel = new PrizePanel(prize);
                    add(panel, c);
                }
            }
        }
    }
}

package cabra.dinero;

import cabra.*;
import javax.swing.*;
import java.awt.*;

/**
 * A panel that contains a prize and draws the prize on it.
 */
public class PrizePanel extends JPanel {

    private ImageIcon image;
    private Color bgColor;
    private static final String PLACEHOLDER_TEXT = "Buy some more prizes to fill up your vault!";
    private static final int BORDER_RADIUS = 10; //rounded corners of the background

    /**
     * Creates a Prize Panel that contains and shows a prize.
     * Pass null to show a question mark (an empty placeholder.)
     */
    public PrizePanel(Prize prize) {
        setLayout(new GridLayout(1, 1));
        //setMinimumSize(new Dimension(MIN_WIDTH, MIN_HEIGHT));

        if (prize == null) {
            //this is a placeholder; show a question mark
            this.image = ImageManager.createImageIcon("prizes/question.png");
            JLabel imageLabel = new JLabel(image);
            imageLabel.setToolTipText(PLACEHOLDER_TEXT);
            add(imageLabel);
        } else {
            //add label containing the image
            this.image = prize.getImage();
            JLabel imageLabel = new JLabel(image);
            imageLabel.setToolTipText(prize.getName() + " - " + prize.getPrizeType().getRarityName());
            add(imageLabel);

            //draw bg later based on the type of prize
            this.bgColor = prize.getPrizeType().getColor();
        }
    }

    public void paintComponent(Graphics g) {
        if (bgColor != null) {
            //draw the background in a rounded rectangle; take up the whole space
            g.setColor(bgColor);
            g.fillRoundRect(0, 0, getWidth(), getHeight(), BORDER_RADIUS, BORDER_RADIUS);
        }
    }
}

package cabra.dinero;

import java.util.ArrayList;
import java.util.Arrays;

/**
 * Contains a list of Prize Packs, which contain prizes of varying rarity.
 * When you buy a Prize Pack, it opens up and a certain number of prizes pop up - you've just won them.
 */
public class PrizePack{
  private static final int PRIZES_PER_PACK = 3;
  private static final int RARITY_DEVIATION = 3; //prizes in this pack have rarities within this.rarity +- deviation
    
  /**
   * A list of every prize pack. Ensure this is sorted.
   */
  public static final PrizePack[] PRIZEPACKS = new PrizePack[]{
    new PrizePack  (2, 50),
      new PrizePack(4, 100),
      new PrizePack(6, 250),
      new PrizePack(8, 500),
      new PrizePack(10, 1000),
      new PrizePack(12, 2000),
      new PrizePack(14, 3000),
      new PrizePack(16, 4000),
      new PrizePack(18, 5000),
      new PrizePack(20, 6000),
      new PrizePack(22, 8000),
      new PrizePack(25, 10000)
  };
  
  private int rarity;
  private int cost;
  
  /**
   * Creates a PrizePack with the given rarity and cost.
   * @param rarity how rare, on average, are the prizes given by the prize pack.
   * @param cost how much it costs to purchase the prize pack.
   */
  private PrizePack(int rarity, int cost){
    this.rarity = rarity;
    this.cost = cost;
  }
  
  public int getRarity(){ return rarity; }
  public int getCost(){ return cost; }
  
  /**
   * Opens up this prize pack, returning a few random prizes of varying rarity.
   */
  public Prize[] retrievePrizes(){
    Prize[] prizes = new Prize[PRIZES_PER_PACK];
    //<TODO>: prevent duplicates
    for(int i=0; i<PRIZES_PER_PACK; i++){
      prizes[i] = Prize.getSuitablePrize(this.rarity, RARITY_DEVIATION);
    }
    
    return prizes;
  }
  
  /**
   * Returns the number of stars this pack has, based on its rarity.
   * The number of stars is in the range [1, maxStars] (inclusive.)
   * @param maxStars the number of stars this should have if it has maximum rarity. At other rarities this will be used as a scale.
   * @return int the number of stars, between 1 and maxStars.
   */
  public int getStars(int maxStars){
    //rarest pack will have most stars, so scale accordingly
    int maxRarity = getRarestPack().getRarity();
    //proportion: this rarity / max rarity = this stars / max stars
    //or this stars = this rarity * max stars / max rarity
    double rawStars = 1.0 * this.rarity * maxStars / maxRarity;
    //round accordingly, but ensure it's at least 1
    int stars = (int)Math.round(rawStars);
    if(stars < 1) stars = 1;
    return stars;
  }
  
  /**
   * @param points the number of points the user has/feels like spending.
   * @return true if you can buy this prize pack with that number of points, false otherwise.
   */
  public boolean canUserAfford(int points){
    return cost <= points;
  }  
  
  /**
   * Returns a list of each Prize Pack available.
   */
  public static ArrayList<PrizePack> getAllPrizePacks(){
    ArrayList<PrizePack> packs = new ArrayList<PrizePack>();
    packs.addAll(Arrays.asList(PRIZEPACKS));
    return packs;
  }
  
  private static PrizePack getRarestPack(){
    //because it's already sorted, just return the last one in the list
    return PRIZEPACKS[PRIZEPACKS.length - 1];
  }
}

package cabra.dinero;

import cabra.*;
import javax.swing.*;
import java.awt.*;
import java.awt.event.*;
import java.util.ArrayList;
import java.util.Arrays;

/**
 * Model for a prize, or an stuffed animal the user can buy.
 */
public enum Prize {

    SQUIRREL("Squirrel", 1),
    DOG("Dog", 2),
    CAT("Cat", 3),
    SHEEP("Sheep", 4),
    COW("Cow", 5),
    PIG("Pig", 6),
    CHICKEN("Chicken", 7),

    DOG2("Dog", 8),
    CAT2("Cat", 9),
    SHEEP2("Sheep", 10),
    COW2("Cow", 11),
    PIG2("Pig", 12),
    CHICKEN2("Chicken", 13),
    PENGUIN("Penguin", 14),
    ELEPHANT("Elephant", 15),

    SHEEP3("Sheep", 16),
    COW3("Cow", 17),
    PIG3("Pig", 18),
    CHICKEN3("Chicken", 19),
    PENGUIN2("Penguin", 20),
    ELEPHANT2("Elephant", 21),
    LION("Lion", 22),
    TIGER("Tiger", 23),

    GOAT("Goat", 24),
    EVIL_GOAT("Evil Goat", 25);



    private final int rarity;
    private final ImageIcon image;
    private final String name;

    /**
     * Full constructor. DON'T CALL THIS DIRECTLY. Use the constructor sans image.
     */
    Prize(String name, int rarity, ImageIcon image) {
        this.name = name;
        this.rarity = rarity;
        this.image = image;
    }

    /**
     * Convenience overload for Prize(int, ImageIcon, name). Pass the name of the image isntead of the imageicon itself.
     * DON'T CALL THIS DIRECTLY EITHER. Use the constructor sans image.
     */
    Prize(String name, int rarity, String imageURL) {
        this(name, rarity, ImageManager.createImageIcon(imageURL));
    }

    /**
     * Super-convenient overload for Prize constructor. The image URL will be the lowercased version of the name + ".png".
     */
    Prize(String name, int rarity) {
        this(name, rarity, "prizes/" + name.replace(' ', '-').toLowerCase() + ".png");
    }

    public ImageIcon getImage() {
        return image;
    }

    public String getName() {
        return name;
    }

    /**
     * Returns the unique identification string of the Prize.
     */
    public String getID() {
        return name();
    }

    public int getRarity() {
        return rarity;
    }

    /**
     * Returns the prize type that fits this prize.
     */
    public PrizeType getPrizeType() {
        return PrizeType.getPrizeType(this);
    }

    public static ArrayList<Prize> getAllPrizes() {
        ArrayList<Prize> prizes = new ArrayList<Prize>();
        prizes.addAll(Arrays.asList(Prize.values()));
        return prizes;
    }

    /**
     * Returns the prize with the given prize ID, or null if there is none.
     * The prize ID must be unique to the prize; use getID() to find a prize's ID.
     */
    public static Prize getPrizeByID(String prizeID) {
        try {
            Prize prize = Prize.valueOf(prizeID);
            return prize;
        } catch (IllegalArgumentException e) {
            return null;
        }
    }

    /**
     * Returns a random suitable prize. The prize's rarity will be in the range [rarity-deviation, rarity+deviation].
     * This is guaranteed to return at least one prize.
     */
    public static Prize getSuitablePrize(int rarity, int deviation) {
        //create list of suitable prizes
        ArrayList<Prize> suitablePrizes = new ArrayList<Prize>();
        for (Prize prize : getAllPrizes()) {
            if (prize.rarity >= rarity - deviation && prize.rarity <= rarity + deviation) {
                suitablePrizes.add(prize);
            }
        }

        //choose random one from the list
        int random = (int) (suitablePrizes.size() * Math.random());
        return suitablePrizes.get(random);
    }

    /**
     * Prize is an enum and hence can't use compareTo(), so we'll use this.
     */
    public static int compare(Prize a, Prize b) {
        return a.getRarity() - b.getRarity();
    }

    /**
     * Based on its rarity, each prize has a type, or color, and a rarity name associated with it.
     * This way the same kind of prize (same animal) can have souped-up versions.
     */
    public enum PrizeType {

        BLUE(7, "0094FF", "Common"),
        RED(15, "FF0000", "Uncommon"),
        GREEN(23, "00E500", "Rare"),
        GOLD(30, "FFC800", "Super Rare");
        /*

        //New point values?
        4 
        7
        10
        13
        15
        18
        21
        23
        25

         */
        private int maxRarity;
        private Color color;
        private String rarityName;

        /**
         * Creates a prize type.
         * The color text is a 6-letter hex string (FF0000) which will map to a color.
         * The rarity name is like "Common" or "Uncommon"; will be displayed on the prize's display.
         */
        PrizeType(int maxRarity, String colorText, String rarityName) {
            this.maxRarity = maxRarity;
            this.color = ColorManager.createColor(colorText);
            this.rarityName = rarityName;
        }

        public int getMaxRarity() {
            return maxRarity;
        }

        public Color getColor() {
            return color;
        }

        public String getRarityName() {
            return rarityName;
        }

        /**
         * Returns the prize type which should fit the given prize.
         * The prize type is based on the prize's rarity and which range it falls in.
         */
        public static PrizeType getPrizeType(Prize prize) {
            PrizeType[] prizeTypes = PrizeType.values();
            for (int i = 0; i < prizeTypes.length; i++) {
                if (prize.getRarity() <= prizeTypes[i].getMaxRarity()) {
                    return prizeTypes[i];
                }
            }

            //it's above the max rarity of any prize type; just give the best one
            return prizeTypes[prizeTypes.length - 1];
        }
    }
}

