 * To change this template, choose Tools | Templates
 * and open the template in the editor.
 */

package cabra;

/**
 *
 * @author Neel
 */

import java.util.ArrayList;
import java.io.*;
import javax.swing.*;

public class Project implements Comparable<Project>{
    private Session session = null; //if a study session is going on, it's here
    private Deck deck; //all the cards in this project are here
    private String name; //the name of this project, like "History Test"
    //private Card currentCard; //just for tracking, the current card you're on
    private ArrayList<Note> notes; //each project has its own notes

    public Project(String name){
        //name is something like "History Test"
        this.name = name;
        deck = new Deck();
        notes = new ArrayList<Note>();
    }

    /* SESSION STUFF */
    public void setSession(Session session){
        this.session = session;
    }

    /** Creates a new session for this project.
     * 
     */
    public void newSession(){
        do{
            setSession(new Session(this));
        }
        while(getSession().isEmpty());
    }

    public Session getSession(){
        return this.session;
    }

    /* NOTE STUFF */

    /**Add an existing note to this project
     * 
     * @param note the note to add
     */
    public void addNote(Note note){
        notes.add(note);
    }

    /** Takes the given note out of the notes list.
     * 
     * @param note the note to remove
     */
    public void removeNote(Note note){
        notes.remove(note);

        //delete the file

        File noteFile = new File(SaveLoad.getProjectFolder() + "/" + name + "/" + note.getName() + "." + Note.EXTENSION);
            //get the note's file name

        //deletes the file
        noteFile.delete();
    }

    /***
     * Returns this project's notes.
     * @return the notes, in ArrayList form
     */
    public ArrayList<Note> getNotes(){
        return notes;
    }

    /**
     * Returns how many notes there are.
     * @return the number of notes
     */
    public int numNotes(){
        return notes.size();
    }

    /* CARD STUFF */

    public void addCard(Card card,Status status){
        card.setStatus(status);
        deck.add(card);

        //if the card has a picture, move the picture over here
        if(card.hasPicture()){
            /*if(!new File(this.getPathTo(card.getPictureName())).exists()){
                //the picture file is corrupted or doesn't exist... remove it
                card.removePicture();

                return;
            }*/

            File copiedFile = copyPictureFile(card.getPictureFile());              

            //resize the image so it's the same size as the studying picture panel; reduces file size
            /** DISABLED so we can see image in full size some time **/
            /*ImageManager.saveImage(ImageManager.scaleImage(
                    GUI.createImageIconFromFullPath(copiedFile.getAbsolutePath()), 
                    PicturePanel.PICTURE_WIDTH,
                    PicturePanel.PICTURE_HEIGHT),
                    copiedFile);*/

            card.setPictureName(copiedFile.getAbsolutePath());
            //and now trim the card's picture file... we won't need the full path any more
            card.trimPictureFile();
        }        

        saveCards();

        //since there's a new card, notify the session
        if(session != null){
            session.update();
        }
    }

    public void addCard(Card card){
        //we don't know if it's important or not
        //however, the card knows if it's important or not... let's ask
        addCard(card,card.getStatus()); //ask the card if it's importnat
    }

    public void addCards(ArrayList<Card> givenCards){
        //called during initialization to create cards
        //significantly reduces overhead by only saving at end
        for(Card card : givenCards){ //go through each card
            deck.add(card);
        }
        //now that the cards have been added, shuffle and save
        shuffle();

        //session = new Session(this);
    }

    /** Removes the given card from the card array... that's it. Well, it also saves.
     * 
     * @param cardToRemove the card to get rid of
     */

    public void removeCard(Card cardToRemove){
        deck.remove(cardToRemove);
        //if the card being removed was active (it probably was), set active card to null
        if(cardToRemove.equals(deck.getCurrentCard())){
            deck.makeCurrentCardNull();
        }
        //delete the card's picture, if it has one
        if(cardToRemove.hasPicture()){
            String path = getPathTo(cardToRemove.getPictureName()); //finds the full path to the image
            File fileToRemove = new File(path);
            //delete the file
            fileToRemove.delete();
        }
        //save
        saveCards();
    }

    /** Copies the given picture file to this guy's folder.
     * 
     * @param pictureFile the picture file to be copied
     * @return the new location of the file
     */

    public File copyPictureFile(File pictureFile){
        String fileName = pictureFile.getName();
        File newFile = new File(SaveLoad.getProjectFolder() + "/" + name + "/" + fileName);
        ImageManager.copyImage(pictureFile,newFile);
        return newFile;
    }

    /** Retrieves an imageicon that is stored in this project's folder
     * 
     * @param imageName the name of the icon (foo.png)
     * @return the created imageicon, or null if the image cannot be found
     */

    public ImageIcon getImageIcon(String imageName){
        return GUI.createImageIconFromFullPath(getPathTo(imageName));   
    }

    /** Finds the absolute location of a card/note based on its name
     * 
     * @param thing the name of the card/note/picture's file, like foo.png
     * @return the full path to foo.png
     */

    public String getPathTo(String thing){
        String folderPath = SaveLoad.getProjectFolder().getAbsolutePath() + "/" + this.getName(); //to the folder of the image
        String absolutePath = folderPath + "/" + thing; //the absolute path to the image        
        return absolutePath;
    }

    public void save(){
        //called when this project needs to be saved

        //the methods are split up for convenience
        saveCards();

        saveNotes();
    }

    public void saveCards(){
        //save all the cards
        //new Thread(new Runnable(){
        //    public synchronized void run(){
              try{
                    BufferedWriter writer = new BufferedWriter(new FileWriter(new File(SaveLoad.getProjectFolder().getAbsolutePath() + "/" + name + "/cards.txt"))); //write to my card file
                    for(Card card : deck.getCards()){
                        //write down each card
                        writer.write(card.toString()); //card's toString() does that question/answer thing
                        writer.newLine();
                    }
                    writer.close();
                }
                catch(IOException io){
                    System.out.println("Couldn't save cards!");
               }     
         //   }
        //}).start();
    }

    public void saveNotes(){
        //save notes
        final Project proj = this;
        //save in background
        new Thread(new Runnable(){
            public void run(){
                SaveLoad.saveNotes(proj);
            }
        }).start();
        SaveLoad.saveNotes(this);
    }

    /** Tells this project to load notes from the saved files. Call this when switching to a new active project
     * 
     */

    public void loadNotes(){
        notes = SaveLoad.getNotesFromProject(this);
    }

    public void shuffle(){
        deck.shuffle();
        saveCards();
    }

    public String getName(){
        return name;
    }

    public void setName(String newName){
        File folder = getFolder(); //folder of this project with old name

        this.name = newName;

        //rename project's folder
        folder.renameTo(new File(SaveLoad.getProjectFolder() + "/" + newName));
    }

    /***
     * Prints out this project's cards. The user earns some points by doing this.
     * @param controller the controller. Used to gain points.
     */
    public void print(Controller controller){
        Printer.print(this, getCards());   

       //earn the points
       controller.gainPoints(PointEnums.Activity.PRINT_CARDS);
    }

    /** Resets all cards in this deck to not studied
     * 
     */
    public void resetAllCards(){
        for(Card card : deck.getCards()){
            card.setStatus(Status.DEFAULT_STATUS);
        }
        saveCards();
    }

    /** The entire session was skipped.
     * 
     */
    public void skipAll(){
        for(Card card : getCards()){
            card.skip();
        }
    }

    public ArrayList<Card> getCards(){
        return deck.getCards();
    }

    /***
     * Returns true if and only if there are 0 cards in the project.
     * @return true if there are 0 cards, false otherwise
     */
    public boolean isEmpty(){
        return numCards() == 0;
    }

    public int numCards(){
        //returns the number of cards in the card list
        return deck.numCards();
    }

    /** Returns, for example, how many not studied cards there are.
     * 
     * @param status the status to check for (learned, not learned, not studied)
     * @return the number of matching cards
     */
    public int numMatchingCards(Status status){
        return deck.numMatchingCards(status);
    }

    /** Returns the statuses of the cards: [A,B,C,D,E]
     * 
     * @return [cards with status A, cards with B, C, D, E]
     */
    public int[] cardStatuses(){
        return new int[]{
            numMatchingCards(Status.A),
            numMatchingCards(Status.B),
            numMatchingCards(Status.C),
            numMatchingCards(Status.D),
            numMatchingCards(Status.E)
        };
    }

    public Card nextCard(){
        return deck.getCard();
    }

    public File getFolder(){
        return new File(SaveLoad.getProjectFolder().getAbsolutePath() + "/" + name);
    }

    public Card getCurrentCard(){
        return deck.getCurrentCard();
    }

    public int getCurrentIndex(){
        return deck.getCurrentIndex();
    }

    @Override
            public String toString(){
                //like "History Test"
                return name; //this guy's toString is just his name
            }
    @Override
            public boolean equals(Object aProject){
                if(aProject == null) return false;
                if(aProject instanceof Project == false)return false;
                try{
                    Project project = (Project)aProject;
                    //if(project == null) return false;
                    return project.name.equals(this.name);//compare by name, i.e. "History Test"                    
                }
                catch(Exception e){
                    return false;
                }
            }

    /**
     * Compares the two projects based on name, case insensitive. "ABC" > "XYZ".
     * @param other
     * @return +ve if this project is bigger than other, -ve if it's smaller, 0 if they are equal (names are the same)
     */
    public int compareTo(Project other){
        if(this.equals(other)) return 0;
        String thisname = this.name.toLowerCase();
        String othername = other.name.toLowerCase();
        return thisname.compareTo(othername);
    }

    @Override
    public int hashCode() {
        int hash = 3;
        hash = 19 * hash + (this.name != null ? this.name.hashCode() : 0);
        return hash;
    }


    public static void createSampleProject(Controller controller){
        Project project = controller.addProject("Sample", true);
        ArrayList<Card> cards = new ArrayList<Card>();
        cards.add(new Card(
                "What is the ultimate answer to life, the universe, and everything?", 
                "42"));
        project.addCards(cards);

        //return project;
        controller.refresh();
    }
}

/*
 * To change this template, choose Tools | Templates
 * and open the template in the editor.
 */

package cabra;

import javax.swing.*;
import javax.swing.table.*;
import java.awt.*;
import java.util.ArrayList;
import java.text.MessageFormat;
import java.awt.print.PrinterException;

/** Provides the ability to print cards or notes of a project.
 *
 * @author Neel
 */
public class Printer extends Object{

        //constants
        public static final int ROW_HEIGHT = 90; //height of each row
        public static final int IMAGE_WIDTH = 60; //for now the image is resized square    

    private Printer(){}

    /** Prints all the notes of a given project.
     * 
     * @param project the active project.
     */
    public static void printNotes(final Project project){

        //a table to hold the text panes
        JTable table = new JTable();

        //get each note
        for(Note note : project.getNotes()){
            //create a text pane and put the note in it
            JTextPane textPane = new JTextPane();
            textPane.setDocument(note.getDocument());

            table.add(textPane);
        }

        //do print
        try {
            boolean complete = table.print( JTable.PrintMode.FIT_WIDTH,
                                            new MessageFormat(project.getName()), //MessageFormat header
                                            new MessageFormat("Cabra - Page {0}"), //MessageFormat footer
                                            true, //show printing dialog
                                            null, //PrintRequestAttributeSet
                                            true, //interactive
                                            null  //PrintService
                                            );
            if (complete) {
               //done

            } else {
                // printing was cancelled 

            }
        } catch (PrinterException pe) {
            // Printing failed, report to the user
            System.out.println(pe);
        }        
    }

    /** Prints the given list of cards (from a project, maybe?)
     * 
     * @param project the project that owns the cards
     * @param cards the cards to print //we're not taking directly from the project since we want filters possible
     */
    public static void print(final Project project, final ArrayList<Card> cards){
        SwingUtilities.invokeLater(new Runnable(){
            public void run(){
                Printer.realPrint(project,cards);
            }
        });
    }

    /** This does the real work, but print() lets this run in its own thread.
     * 
     * @param project the project that owns the cards
     * @param cards the cards to print
     */
    private static void realPrint(final Project project, final ArrayList<Card> cards){

        //this contains each and every card with its contents
        Object[][] contents = new Object[cards.size()][3]; //cards down, image/question/answer acrs
        for(int i=0;i<cards.size();i++){
            Card card = cards.get(i); 
            //extract card's contents and add to the contents
            if(card.getPictureName().equals(Card.NO_PICTURE_STRING)){
                //no picture, so leave the image box empty
                contents[i][0] = null;
            }
              else{
                //there IS a picture, so put it in here
                contents[i][0] = project.getPathTo(card.getPictureName());
            }

            //<html> forces line wrap
            final String inFront = "<html>"; 
            contents[i][1] = inFront + java.util.regex.Matcher.quoteReplacement(card.getQuestion()).
                    replaceAll(Card.NEWLINE,"<br>");
            contents[i][2] = inFront + java.util.regex.Matcher.quoteReplacement(card.getAnswer()).
                    replaceAll(Card.NEWLINE,"<br>");
        }

        //create table
        final JTable table = new JTable(new CardModel(contents));       
        //table = new JTable(contents, new Object[]{"Image","Question","Answer"}); 

        table.setShowGrid(true);
        table.setRowHeight(ROW_HEIGHT);
        //table.setFillsViewportHeight(true);

        //edit image cols 
        table.getColumn("Image").setMinWidth(IMAGE_WIDTH); //first column is image column; make it as big as an image
        table.getColumn("Image").setMaxWidth(IMAGE_WIDTH); 
        table.getColumn("Image").setCellRenderer(new ImageRenderer()); 

        table.getColumn("Question").setCellRenderer(new CenterRenderer());
        table.getColumn("Answer").setCellRenderer(new CenterRenderer());

        //print preview
        final JDialog dialog = new JDialog((JFrame)null,"Preview of " + project.getName());
        dialog.setIconImage(GUI.createImageIcon("printer.png").getImage());
        dialog.setContentPane(new JScrollPane(table));
        dialog.setSize(500,500);
        Utils.changeFrameLocation(dialog,150,150);
        dialog.setVisible(true);

        //these don't work
        //dialog.repaint();
        //dialog.validate();

        SwingUtilities.invokeLater(new Runnable(){public void run(){
        //do print
        try {
            boolean complete = table.print( JTable.PrintMode.FIT_WIDTH, //printing style
                                            new MessageFormat(project.getName()), //MessageFormat header
                                            new MessageFormat("Cabra - Page {0}"), //MessageFormat footer
                                            true, //show printing dialog
                                            null, //PrintRequestAttributeSet
                                            true, //interactive
                                            null  //PrintService
                                            );
            //boolean complete = table.print();
            if (complete) {
               //done

            } else {
                // printing was cancelled

            }
        } catch (PrinterException pe) {
            // Printing failed, report to the user
            System.out.println(pe);
        }
        finally{
            dialog.setVisible(false);
        }
        }});
    }

    private static class CardModel implements TableModel {
        private Object[][] DATA; //card information here

        public CardModel(Object[][] data){
            this.DATA = data;
        }

        public void setValueAt(Object aValue, int rowIndex, int columnIndex) {}
        public void addTableModelListener(javax.swing.event.TableModelListener l) {}
        public void removeTableModelListener(javax.swing.event.TableModelListener l) {}

        public boolean isCellEditable(int rowIndex, int columnIndex) {
            return false;
        }

        public Class<?> getColumnClass(int col) {
            switch(col) {
                case 0:
                    return ImageIcon.class;
                case 1:
                case 2:
                    return String.class;
            }

            throw new AssertionError("invalid column");
        }

        public int getRowCount() {
            return DATA.length;
        }

        public int getColumnCount() {
            return 3;
        }

        public String getColumnName(int col) {
            switch(col) {
                case 0: return "Image";
                case 1: return "Question";
                case 2: return "Answer";
            }

            throw new AssertionError("invalid column");
        }

        public Object getValueAt(int row, int col) {
            switch(col) {
                case 0:
                case 1:
                case 2:
                    return DATA[row][col];
            }

            throw new AssertionError("invalid column");
        }
    }    

    /**
     * A custom cell renderer for rendering images.
     */
    private static class ImageRenderer extends DefaultTableCellRenderer {
            public Component getTableCellRendererComponent(JTable table,
                                                           Object value,
                                                           boolean isSelected,
                                                           boolean hasFocus,
                                                           int row,
                                                           int column) {

            super.getTableCellRendererComponent(table, value, isSelected,
                                                hasFocus, row, column);

            if(getText().equals("") == false){
                //only do this if there is an image here
                setHorizontalAlignment(SwingConstants.CENTER);

                //use the text (which is full path to image) to make image
                ImageIcon fullSizeIcon = GUI.createImageIconFromFullPath(this.getText());
                ImageIcon resizedIcon = ImageManager.scaleImage(fullSizeIcon, IMAGE_WIDTH,IMAGE_WIDTH); 
                setIcon(resizedIcon);
            }
            else{
                //no image
                setIcon(null);
            }

            //clean up
            setText("");

            return this;
        }
    }

    /**
     * A custom cell renderer that centers text.
     */
    private static class CenterRenderer extends DefaultTableCellRenderer {
            public Component getTableCellRendererComponent(JTable table,
                                                           Object value,
                                                           boolean isSelected,
                                                           boolean hasFocus,
                                                           int row,
                                                           int column) {

            super.getTableCellRendererComponent(table, value, isSelected,
                                                hasFocus, row, column);

            //setHorizontalAlignment(SwingConstants.CENTER);
            //setVerticalAlignment(SwingConstants.CENTER);

            //while we're at it, set a nicer font
            setFont(FontManager.SMALLER_PREFERRED_FONT);

            return this;
        }
    }
}

package cabra;

import cabra.dinero.VaultManager;
import cabra.PointEnums.Activity;
import java.awt.event.ActionEvent;
import java.util.EventObject;
import javax.swing.*;
import java.awt.*;
import javax.swing.event.CellEditorListener;
import javax.swing.table.DefaultTableCellRenderer;
import java.util.ArrayList;
import java.awt.event.ActionListener;

/** A panel to show and keep track of how many points the user has.
 *
 * @author Neel
 */
public class PointPanel extends JPanel{
    private Controller controller;
    private VaultManager vaultManager; //controls everything relating to prizes etc
    private GUI gui;
    private TabPane tabPane;
    private JLabel pointLabel;

    private final static int TABLE_WIDTH = 330;
    private final static int TABLE_HEIGHT = 290;

    public PointPanel(TabPane tabPane, final GUI gui, Controller controller){
        this.tabPane = tabPane;
        this.gui = gui;
        this.controller = controller;

        //must build point label first so we can make vault manager, then add other stuff
        pointLabel = new JLabel(ImageManager.createImageIcon("coins-large.png"), SwingConstants.CENTER);
        //label.setFont(FontManager.LARGER_PREFERRED_FONT);
        pointLabel.setFont(FontManager.createFont(28,"Agency FB", "Georgia")); //choose cool fonts that look like LCD displays     
        add(pointLabel);      

        this.vaultManager = controller.createVaultManager(pointLabel);
        vaultManager.updatePointLabel(controller.getPoints());

        //add button that opens achievements table
        JButton achievementButton = new JButton("How to earn points", ImageManager.createImageIcon("coins-plus.png"));
        achievementButton.addActionListener(new ActionListener(){
            public void actionPerformed(ActionEvent e) {
                JPanel panel = new JPanel();
                JScrollPane tablePane = new JScrollPane(createAchievementTable());
                tablePane.setPreferredSize(new Dimension(TABLE_WIDTH, TABLE_HEIGHT));
                panel.add(tablePane);
                JDialog dialog = Utils.putPanelInDialog(panel, gui.getFrame(), "How to earn points", "coins-plus.png", -1, -1);
                dialog.setVisible(true);
            }
        });
        add(achievementButton);

        add(vaultManager.getPanel());              

        //refresh();
    }

    /**
     * Resets the contents of this panel.
     */
    public void refresh(){
        //this.removeAll(); 



        //add achievements table
        /*JScrollPane tablePane = new JScrollPane(createAchievementTable());
        tablePane.setPreferredSize(new Dimension(TABLE_WIDTH, TABLE_HEIGHT));
        add(tablePane);    */    
    }

    /**
     * Creates a JTable where you can see what you can do to earn points.
     * @return a JTable
     */
    private JTable createAchievementTable(){
        JTable table;

        String[] columnNames = {
            "",
            "Coins",
            "How you earn the coins"
        };

        //fill each row with achievement information
        ArrayList<Activity> values = Activity.getNonSecretActivities();
        //determine how many non-secret activities there are
        int numActivities = 0;
        for(Activity activity : values){
            numActivities++;
        }
        Object[][] data = new Object[numActivities][columnNames.length];
        for(int i=0; i<values.size(); i++){
            //the row contains information from the values array of the same index
            data[i] = createActivityRow(values.get(i));
        }

        table = new JTable(data, columnNames){
            //make table read-only
            @Override
              public boolean isCellEditable(int row, int column){
                return false;
              }
            };
        table.getColumnModel().getColumn(0).setCellRenderer(new ImageRenderer());

        //specialize width of column
        javax.swing.table.TableColumn column = null;
        for (int i = 0; i < columnNames.length; i++) {
            column = table.getColumnModel().getColumn(i);
            switch(i){
                case 0:
                    column.setPreferredWidth(20);
                    break;
                case 1:
                    column.setPreferredWidth(50);
                    break;
                case 2:
                    column.setPreferredWidth(300);
                    break;
            }
        }

        table.setRowHeight(25);
        table.setAutoResizeMode(JTable.AUTO_RESIZE_OFF);
        table.setFillsViewportHeight(true);
        return table;
    }

    /**
     * Convenience method for creating an achievement for the achievement table.
     * @param activity the Activity that we will make a row for
     * @return the Object[] representing the row.
     */
    private Object[] createActivityRow(Activity activity){
        PointEnums.Coin coin = PointEnums.Coin.getCoin(activity.getPoints());
        return new Object[]{
            ImageManager.createImageIcon(coin.getImageName()),
            activity.getPoints(),
            activity.getDescription()
        };
    }

    /**
     * For rendering images in a table.
     */
    class ImageRenderer extends DefaultTableCellRenderer {
      JLabel label = new JLabel();

      @Override
      public Component getTableCellRendererComponent(JTable table, Object value, boolean isSelected,
        boolean hasFocus, int row, int column) {
        label.setIcon((ImageIcon)value);
        return label;
      }    
    }

    @Override
        public void paintComponent(Graphics g){
            super.paintComponent(g);

            Utils.drawEmblem(this, g);
        }        
}

package cabra;

import cabra.dinero.VaultManager;
import java.util.ArrayList;

/** Manages "Points", which are earned by doing stuff in Cabra.
 *
 * @author Neel
 */
public class PointManager {

    private int points;
    private VaultManager vaultManager;

    public PointManager(){
        //load points from UserData
        this.points = UserData.getInt("Points");
    }

    public int getPoints(){
        return points;
    }

    private void setPoints(int points){
        this.points = points;
        //save it
        UserData.setString("Points", points + "");
        vaultManager.update();
    }

    /**
     * Decreases the number of points the user has by the given amount.
     * @param pointsToLose the points to lose.
     * @return the number of points the user has left.
     */
    public int decreasePoints(int pointsToLose){
        int pointsLeft = getPoints() - pointsToLose;
        setPoints(pointsLeft);
        return pointsLeft;
    }

    /**
     * Adds points.
     * @param activity an activity that the user just did.
     */
    public void gainPoints(PointEnums.Activity activity){
        int pointsGained = activity.getPoints();

        setPoints(points + pointsGained);
    }

    public void giveVaultManager(VaultManager vaultManager){
        this.vaultManager = vaultManager;
    }
}

